import React from "react";
import { FaPlus } from "react-icons/fa";

//Material-ui stuff
import { makeStyles } from "@material-ui/core/styles";
import TextField from "@material-ui/core/TextField";
import Modal from "@material-ui/core/Modal";
import Grid from "@material-ui/core/Grid";
import TextareaAutosize from "@material-ui/core/TextareaAutosize";

const useStyles = makeStyles((theme) => ({
  paper: {
    position: "absolute",
    width: 400,
    backgroundColor: theme.palette.background.paper,
    border: "2px solid #000",
    boxShadow: theme.shadows[1],
    padding: theme.spacing(2, 6, 2, 2),
    top: "40%",
    left: "30%",
  },
  root: {
    "& > *": {
      margin: theme.spacing(1),
    },
  },
  rootButtonGroup: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    "& > *": {
      margin: theme.spacing(1),
    },
  },
}));

export const ModalPlus = () => {
  const classes = useStyles();

  const [open, setOpen] = React.useState(false);

  const handleOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const body = (
    <div className={classes.paper}>
      <form className={classes.root} noValidate autoComplete="off">
        <Grid container spacing={2}>
          <Grid item xs={12}>
            <TextField fullWidth={true} id="standard-basic" label="Title" />
          </Grid>
          <Grid item xs={12}>
            <div className={classes.rootButtonGroup}>Bold Color Italic</div>
          </Grid>
          <Grid item xs={12}>
            <TextareaAutosize
              className="aria"
              rowsMin={4}
              aria-label="empty textarea"
              placeholder="Empty"
            />
          </Grid>
        </Grid>
      </form>
    </div>
  );

  return (
    <div>
      <FaPlus className="plus" onClick={handleOpen} />
      <Modal
        BackdropProps={{ invisible: true }}
        open={open}
        onClose={handleClose}
        aria-labelledby="simple-modal-title"
        aria-describedby="simple-modal-description"
      >
        {body}
      </Modal>
    </div>
  );
};
